drop table RECORD ;

CREATE TABLE RECORD (
  ID varchar(8) NOT NULL DEFAULT '',
  DATE date DEFAULT NULL,
  FORMULA varchar(30) DEFAULT NULL,
  EXACT_MASS double DEFAULT NULL,
  INSTRUMENT_NO int DEFAULT NULL,
  SMILES LONG VARCHAR,
  IUPAC LONG VARCHAR,
  MS_TYPE varchar(8) DEFAULT NULL,
  PRIMARY KEY (ID)
);







